mapper.select:
select

  distinct


from protocol_poi_subject




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.insert:
insert into protocol_poi_subject (id, protocol_id, poi_id, 
  poi_name, poi_subject_no, protocol_subject_no, 
  protocol_subject_name, create_time, update_time, 
  create_by, update_by, valid
  )
values (#{id,jdbcType=BIGINT}, #{protocolId,jdbcType=BIGINT}, #{poiId,jdbcType=BIGINT}, 
  #{poiName,jdbcType=VARCHAR}, #{poiSubjectNo,jdbcType=VARCHAR}, #{protocolSubjectNo,jdbcType=VARCHAR}, 
  #{protocolSubjectName,jdbcType=VARCHAR}, #{createTime,jdbcType=BIGINT}, #{updateTime,jdbcType=BIGINT}, 
  #{createBy,jdbcType=VARCHAR}, #{updateBy,jdbcType=VARCHAR}, #{valid,jdbcType=INTEGER}
  )
